import React, { Component } from 'react'
import Head from './header';
import fetch from 'isomorphic-unfetch'
import Link from 'next/link'

class Search1 extends Component {
    constructor(props){
        super(props);
        this.state = { txtTieuDePhim: "batman"}
    }
    render() {
        return <div className="container">
            <Head></Head>
            <form action="" method="POST" role="form">
                <div class="form-group">
                    <label>Tiêu đề phim</label>
                    <input type="text" name="txtTieuDePhim" value={this.state.txtTieuDePhim} class="form-control" placeholder="Nhập tiêu đề phim" />
                </div>
                <button type="submit" class="btn btn-primary">Tìm kiếm</button>
            </form>
            <br /><br />
            <div className="row">
                {this.props.shows.Search.map((show, index) => (
                    <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
                        <Link as={`/detail/${show.imdbID}`} href={`/detail?id=${show.imdbID}`}>
                            <img src={show.Poster} style={{ height: 350 }} alt="Image" />
                        </Link>
                        <br />
                        <Link as={`/detail/${show.imdbID}`} href={`/detail?id=${show.imdbID}`}>
                            <h4 style={{ height: 60 }}>{show.Title}</h4>
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    }
    getInitialProps = async function () {
        const res = await fetch(`http://www.omdbapi.com/?s=batman&apikey=dd31b83b`)
        const data = await res.json()
        return {
            shows: data
        }
    }
}




export default Search1
