import Link from 'next/link'
import fetch from 'isomorphic-unfetch'
import Head from './header'

function Index(props) {
    return <div >
        <Head />
        <h1>Danh sach phim</h1>
        {/* <ul>
        {props.shows.Search.map((show, index) => (
           <li key={index}>
             <Link as={`/p/${show.imdbID}`} href={`/post?id=${show.imdbID}`}>
              <a>{show.Title}</a>
             </Link>
           </li>
        ))}
      </ul> */}
        <div className="row">
            {props.shows.Search.map((show, index) => (
                <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                    <Link as={`/p/${show.imdbID}`} href={`/post?id=${show.imdbID}`}>
                        <img src={show.Poster} style={{ height: 300}} alt="Image" />
                    </Link>
                    <br/>
                    <span className="label">{show.Title}</span>

                </div>
                ))}
        </div>
    </div>
}


Index.getInitialProps = async function () {
    const res = await fetch('http://www.omdbapi.com/?s=move&apikey=dd31b83b')
    const data = await res.json()
    return {
        shows: data
    }
}

export default Index
