import Link from 'next/link'
import fetch from 'isomorphic-unfetch'
import Head from './header'

function Index(props) {
    return <div className="container" >
        <Head />
        <h1>Danh sach phim</h1>
        <div className="row">
            {props.shows.Search.map((show, index) => (
                <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
                    <Link as={`/p/${show.imdbID}`} href={`/post?id=${show.imdbID}`}>
                        <img src={show.Poster} style={{ height: 200}} alt="Image" />
                    </Link>
                    <br/>
                    <Link as={`/p/${show.imdbID}`} href={`/post?id=${show.imdbID}`}>
                        <h4>{show.Title}</h4>
                    </Link>
                </div>
                ))}
        </div>
        <br/>

    </div>
}

Index.getInitialProps = async function () {
    const res = await fetch('http://www.omdbapi.com/?s=batman&apikey=dd31b83b')
    const data = await res.json()
    return {
        shows: data
    }
}

export default Index
