import Head from './header';
import React, {Component} from 'next'

class Search extends Component {
    constructor(props) {
        super(props)
        this.state = { txtTieuDePhim: '123' }
    }
    render() {
        return <div className="container">
            <Head></Head>
            <form action="" method="POST" role="form">
                <div class="form-group">
                    <label>Tiêu đề phim</label>
                    <input type="text" name="txtTieuDePhim" value={this.state.txtTieuDePhim} class="form-control" id="" placeholder="Nhập tiêu đề phim" />
                </div>
                <button type="submit" class="btn btn-primary">Tìm kiếm</button>
            </form>
        </div>
    }
}
export default Search
